ges you WILL get.  	$START = ($_GET['page'] - 1) * $DISPPAGE;	mysql_data_seek($result3,$START);  #Moves the pointer to right row     for($i = 1; $i <= $DISPPAGE && $ARET = @mysqli_fetch_assoc($result3); $i++):?>				<div  style="border:solid 1px #F5F5F5; width:210px; float:left; margin:5px;"> <a href="single.php?id_categoria=<?php echo carga_categoriatexto($ARET['categoria']);?>&id_subcategoria=<?php echo carga_subcategoriatexto($ARET['subcategoria']);?>&cod_estilo=<?= $ARET['cod_estilo'];?>">		<div class="view1 view-fifth1">			<div style="padding:5px;">			<h3 class="m_1" style="font-weight:bolder; font-size:75%; margin-bottom:15px; height:30px;"><?= $ARET['nombre_estilo'];?></h3>				<div>              <div class="css3">                 <?php $nombreimagend = "../bimages/".$ARET['cod_estilo'].".jpg"; 					if (file_exists($nombreimagend)) { ?>                <img src="../bimages/<?php echo $ARET['cod_estilo'];?>.jpg"  height="180" width="200"  title="<?php echo $ARET['nombre_estilo'];?>"/>                 <?php }else{?>                <img src="../bimages/ND.jpg" height="180" width="200" title="<?php echo  $ARET['nombre_estilo'];?>"/>                 <?php } ?>              	</div>					<div class="mask1" style=" margin-right:100px;"><div class="info">Ver</div></div>				</div>			</div>		</div>	</a></div><?php endfor;?><?php if ($QNUM > 0) { ; ?><?php echo "<div class=\"clear\" style=\"text-align:center; margin-top:8px;\">";	if (!isset($_GET['inicio'])) {$inicio=1; $_GET['page'] = 1;}else{$inicio = $_GET['inicio']; };	for($i = $inicio; $i <= $NUMPAGES+$inicio; $i++):  #loop to print << 1 2 3... $NUMPAGES >>  	  if($i == $inicio && $_GET['page'] > $fin)  #Prints the << first to goto the previous page (not on page 1)          echo "<a class=negrobold href=\"$PHP_SELF?inicio=".($inicio - $fin)."&page=".($inicio - 1)."&palabra=$palabra&id_subcategoria=".$_GET['id_subcategoria']."&id_categoria=".$_GET['id_categoria']."\" onMouseOver=\"status='Paginas Anteriores';return true;